        if ($this->databaseConnection()) {
            $sql = $this->db_connection->prepare('SELECT DOCUMENT_ID FROM tbldocumentemployeepermission WHERE DOCUMENT_ID = :document_id AND EMPLOYEE_ID = :employee_id AND PERMISSION = :permission');
