    public function get_sales_partner_to_date_booking_details(){
        if ($this->databaseConnection()) {
            $query = "
                WITH
                    sales_partner AS (
                        SELECT
                            pmqh.full_name,
                            SUM(pmq.quota) AS quota
                        FROM
                            position_monthly_quota pmq
                        INNER JOIN
