    # -------------------------------------------------------------
    #
    # Name       : get_modal_size
    # Purpose    : Get the size of the modal.
    #
    # Returns    : String
    #
    # -------------------------------------------------------------
    public function get_modal_size($size){
        if($size == 'SM'){
            return 'modal-sm';
        }
        else if($size == 'LG'){
            return 'modal-lg';
        }
        else if($size == 'XL'){
            return 'modal-xl';
        }
        else {
            return '';
        }
    }
