    # -------------------------------------------------------------
    #
    # Name       : get_role_status
    # Purpose    : Returns the status, badge
    #
    # Returns    : Array
    #
    # -------------------------------------------------------------
    public function get_role_status($stat){
        $response = array();

        switch ($stat) {
            case '1':
                $status = 'ACTIVE';
                $button_class = 'bg-success';
                break;
            default:
                $status = 'INACTIVE';
                $button_class = 'bg-danger';
        }

        $response[] = array(
            'STATUS' => $status,
            'BADGE' => '<span class="badge '. $button_class .'">'. $status .'</span>'
        );

        return $response;
    }
