    # -------------------------------------------------------------
    #
    # Name       : check_week_day
    # Purpose    : Checks the week day
    #
    # Returns    : Date
    #
    # -------------------------------------------------------------
    public function check_week_day($day){
        if($day == 0) {
            return '7';
        }
        else{
            return $day;
        }
    }
