    # -------------------------------------------------------------
    #
    # Name       : check_attendance_clock_out
    # Purpose    : Checks the attendance clock out
    #
    # Returns    : Date
    #
    # -------------------------------------------------------------
    public function check_attendance_clock_out($employee_id){
        if ($this->databaseConnection()) {
            $overlap_count = 0;

