    # -------------------------------------------------------------
    #
    # Name       : generate_year
    # Purpose    : Generates year of dropdown
    #
    # Returns    : String
    #
    # -------------------------------------------------------------
    public function generate_year($start, $end, $sort){
        if ($this->databaseConnection()) {
            $option = '';

            if($sort == 'asc'){
                for($i = $start; $i <= $end; $i++){
                    $option .= '<option value="'. $i .'">'. $i.'</option>';
                }
            }
            else{
                for($i = $start; $i >= $end; $i--){
                    $option .= '<option value="'. $i .'">'. $i.'</option>';
                }
            }

            return $option;
        }
    }
