    # --------------------------------------------------------- ----
    #
    # Name       : generate_file_name
    # Purpose    : generates random file name
    #
    # Returns    : Number
    #
    # -------------------------------------------------------------
    public function generate_file_name($length) {
        $key = '';
        $keys = array_merge(range(0, 9), range('a', 'z'));

        for ($i = 0; $i < $length; $i++) {
            $key .= $keys[array_rand($keys)];
        }

        return $key;
    }
