                        $document_name = trim($row['DOCUMENT_NAME']);
                        $upload_date = $this->check_date('empty', $row['UPLOAD_DATE'], '', 'F d, Y', '', '', '');

                        $option .= "<option value='". $document_id ."'>". $document_name ." - ". $upload_date ."</option>";
